import logo from './logo.svg';
import './App.css';
import Box from "./component/Box"

//2 boxes (title, pic, result)
//가위바위보 버튼이 있다
//버튼을 클릭하면 클릭한 값이 박스에 보임
//컴퓨터는 랜덤하게 아이탬 선택
//3,4의 결과를 가지고 누가 이겼는지 승패를 따진다
//승패 결과에 따라 테두리 색이 바뀐다
function App() {
  return (
    <div>
      <div className='main'>
        <Box title='You'/>
        <Box title='Computer'/>
      </div>
      <div className='main'>
        <button>rock</button>
        <button>scissors</button>
        <button>paper</button>
      </div>
    </div>
  );
}

export default App;
